    private void btnSomaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnSomaActionPerformed
        int x11 = Integer.parseInt(x1.getText());
        int y11 = Integer.parseInt(y1.getText());
        int z11 = Integer.parseInt(z1.getText());
        int i11 = Integer.parseInt(i1.getText());
        int x12 = Integer.parseInt(x2.getText());
        int y12 = Integer.parseInt(y2.getText());
        int z12 = Integer.parseInt(z2.getText());
        int i12 = Integer.parseInt(i2.getText());
        int x13 = Integer.parseInt(x3.getText());
        int y13 = Integer.parseInt(y3.getText());
        int z13 = Integer.parseInt(z3.getText());
        int i13 = Integer.parseInt(i3.getText());
        int d = (x11*y12*z13)+(y11*z12*x13)+(z11*x12*y13)-(z11*y12*x13)-(x11*z12*y13)-(y11*x12*z13);
        int dx = (i11*y12*z13)+(y11*z12*i13)+(z11*i12*y13)-(z11*y12*i13)-(i11*z12*y13)-(y11*i12*z13);
        int dy = (x11*i12*z13)+(i11*z12*x13)+(z11*x12*i13)-(z11*i12*x13)-(x11*z12*i13)-(i11*x12*z13);
        int dz = (x11*y12*i13)+(y11*i12*x13)+(i11*x12*y13)-(i11*y12*x13)-(x11*i12*y13)-(y11*x12*i13);
        
        int x = (dx/d);
        int y = (dy/d);
        int z = (dz/d);
        rx.setText(Integer.toString(x));
        ry.setText(Integer.toString(y));
        rz.setText(Integer.toString(z));
        
        
               
    }//GEN-LAST:event_btnSomaActionPerformed

    private void btnSoma2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnSoma2ActionPerformed
        int x21 = Integer.parseInt(x1.getText());
        int x22 = Integer.parseInt(x2.getText());
        int y21 = Integer.parseInt(y1.getText());
        int y22 = Integer.parseInt(y2.getText());
        int i21 = Integer.parseInt(i1.getText());
        int i22 = Integer.parseInt(i2.getText());
        int dd = (x21*y22) - (y21*x22);
        int ddx = (i21*y22) - (y21*i22);
        int ddy = (x21*i22) - (i21*x22);
        
        int xx = (ddx/dd);
        int yy = (ddy/dd);
        rx.setText(Integer.toString(xx));
        ry.setText(Integer.toString(yy));
  
        
    }//GEN-LAST:event_btnSoma2ActionPerformed

